import { MdOutlineAdd } from 'react-icons/md';
const AddTodo = ({ onSubmit, onChange, todo, message }) => {
  return (
    <form onSubmit={onSubmit} autoComplete="off">
      <input type="text" value={todo ?? ''} onChange={onChange} />
      <button type="submit">
        <MdOutlineAdd />
      </button>
      <h1>할일 등록</h1>
    </form>
  );
};
export default AddTodo;
