import { useState, useRef } from 'react';
import AddTodo from '../components/AddTodo';
import TodoList from '../components/TodoList';

const initialValue = [
  {
    id: 1,
    title: '할일 1',
    done: true,
  },
  {
    id: 2,
    title: '할일 2',
    done: false,
  },
  {
    id: 3,
    title: '할일 3',
    done: false,
  },
];
const TodoContainer = () => {
  const [items, setItems] = useState(initialValue);
  const [todo, setTodo] = useState('');

  let id = useRef(4); //할일 id
  let todoEl;
  const onSubmit = (e) => {
    e.preventDefault();
    if (!todo.trim()) {
      alert('할일을 입력하세요');
      return 0;
    }
    //입력시 새로운 item리스트 만들고
    const newItems = items.concat({
      id: id.current,
      title: todo.trim(),
      done: false,
    });
    //newitems으로 할당
    setItems(newItems);
    id.current++;
    //입력시 창 초기화
    setTodo('');
  };
  //할일 입력시 todo 변경
  const onChange = (e) => setTodo(e.currentTarget.value);
  return (
    <>
      <AddTodo
        onSubmit={onSubmit}
        onChange={onChange}
        todo={todo}
        todoEl={todoEl}
      />
      <TodoList items={items} />
    </>
  );
};
export default TodoContainer;
